package com.example.sonai.portfolio;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.TextView;import com.example.sonai.portfolio.models.DatabaseHelper;import com.example.sonai.portfolio.models.Monster;public class MonsterUCActivity extends AppCompatActivity {    private Button m_cSave;    private Button m_cUpdate;    private EditText m_sName;    private EditText m_iAge;    private EditText m_sSpecies;    private EditText m_iAttackPower;    private EditText m_fHealth;    private TextView m_sResult;    private Monster m_cMonster;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_monster_uc);        m_cMonster = new Monster();        m_sName = (EditText) findViewById(R.id.name);        m_iAge = (EditText) findViewById(R.id.age);        m_sSpecies = (EditText) findViewById(R.id.species);        m_iAttackPower = (EditText) findViewById(R.id.attackPower);        m_fHealth = (EditText) findViewById(R.id.health);        m_cSave = (Button) findViewById(R.id.save);        m_cUpdate = (Button) findViewById(R.id.update);        m_sResult = (TextView) findViewById(R.id.resultLabel);        m_cSave.setOnClickListener(new View.OnClickListener(){            @Override            public void onClick(View v) {                Monster monster = new Monster(0,m_sName.getText().toString()                            ,new Integer(m_iAge.getText().toString())                            ,m_sSpecies.getText().toString()                            ,new Integer(m_iAttackPower.getText().toString())                            ,new Float(m_fHealth.getText().toString()));                DatabaseHelper db = new DatabaseHelper(getApplicationContext());                db.addMonster(monster);                m_sResult.setText(monster.summary());            }        });    }}